mapper.select:
select * from ( select a.*, rownum as rowno from( select distinct u.id as id, NVL(u.user_name,' ') as userName, NVL(u.fuzz,' ') as fuzz, u.login_name as loginName , NVL(u.user_sex,' ') as userSex, u.user_statu as userStatu, NVL(d.dept_name,' ') as deptName, u.upd_time, NVL((select wm_concat(o.role_name) from user_role r inner join role o on r.role_id= o.id where r.use_id = u.id),' ') as uRole from users u inner join dept d on d.id = u.user_dept left join user_role r on r.use_id =u.id
u.SFGLY != '1'
and u.del_flag =#{t.delFlag}
and r.role_id = #{t.uRole}
and u.user_name like '%'||#{t.userName}||'%'
START WITH d.id = #{t.userDept} CONNECT BY d.parent_id = PRIOR d.id
order by u.upd_time )a where rownum <= #{pageNo} * #{pageSize} )b where b.rowno >= (#{pageNo} - 1) * #{pageSize}

select count(1) from (select distinct u.id as id, NVL(u.user_name,' ') as userName, NVL(u.fuzz,' ') as fuzz, u.login_name as loginName , NVL(u.user_sex,' ') as userSex, u.user_statu as userStatu, NVL(d.dept_name,' ') as deptName, u.upd_time, NVL((select wm_concat(o.role_name) from user_role r inner join role o on r.role_id= o.id where r.use_id = u.id),' ') as uRole from users u inner join dept d on d.id = u.user_dept left join user_role r on r.use_id =u.id
u.SFGLY != '1' and u.del_flag =#{delFlag}
and r.role_id = #{uRole}
and u.user_name like '%'||#{userName}||'%'
START WITH d.id = #{userDept} CONNECT BY d.parent_id = PRIOR d.id ) a

mapper.insert:
insert into 
user_role(
	USE_ID,
	ROLE_ID
) (
	
		select 
			#{role.id},
			#{role.roleId}
		from dual
	
)

