	/**
	 * 打印单个
	 * 
	 * @param srln
	 *            流水号
	 * @param rckCount
	 *            复检次数
	 * @throws SQLException
	 * @throws IOException
	 */
	public void dyOne(String srln, String rckCount) throws Exception {
		IBgdDyDao bgdDyDao = SpringContextHolder.getBean(IBgdDyDao.class);
		Map<String, Object> map = bgdDyDao.findCkOne(srln, rckCount);
		if (map == null || map.size() < 1) {
			return;
		}
		dyyw(map);
		String fileName = map.get("srln").toString()
				+ map.get("rckCount").toString() + "i.doc";
		String parentSyxz = bgdDyDao
				.findParentSyxz(map.get("syxzx").toString());

		// 校车打印
		String cllxs = map.get("cllxs").toString();
		if ("K38".equals(cllxs) || "K28".equals(cllxs) || "K18".equals(cllxs)) {
			// 专用校车
			map.put("zyxc", "☑");
			map.put("fzyxc", "□");
			WordUtil.createWord(map, "/xcjlb.ftl",
					PropertiesUtils.getValues("printpath"), fileName,
					"/templete");
		} else if ("U".equals(parentSyxz)) {
			// 非专用校车，确做校车使用的车型
			map.put("zyxc", "□");
			map.put("fzyxc", "☑");
			WordUtil.createWord(map, "/xcjlb.ftl",
					PropertiesUtils.getValues("printpath"), fileName,
					"/templete");
		} else {
			// 非校车 激动车打印
			WordUtil.createWord(map, "/cyjlb.ftl",
					PropertiesUtils.getValues("printpath"), fileName,
					"/templete");
		}
		// 打印
		dyto(PropertiesUtils.getValues("printpath") + fileName,
				PropertiesUtils.getValues("recordPrinterName"));
		final String pathsString = PropertiesUtils.getValues("printpath")
				+ fileName;

		FileUtils.deleteFile(pathsString);
		bgdDyDao.updatedyStatu(map.get("srln").toString(), map.get("rckCount")
				.toString());
	}

