	/**
	 * 生成文档 
	 * 
	 * @return
	 */
	public String geneteJlWord(Map<String, Object> map,String savePath){
		String templeteName="";
		String fileName = map.get("srln").toString() + map.get("rckCount").toString() + "i.doc";
		getDyJlData(map);	
		String parentSyxz = bgdDyDao.findParentSyxz(map.get("syxzx").toString());
		String cllxs = map.get("cllxs").toString();	
		if ("K38".equals(cllxs) || "K28".equals(cllxs) || "K18".equals(cllxs)) {
			// 专用校车
			templeteName="/xcjlb.ftl";
		} else if ("U".equals(parentSyxz)) {
			// 非专用校车，确做校车使用的车型
			templeteName="/xcjlb.ftl";

		} else {
			templeteName="/cyjlb.ftl";
		}
		WordUtil.createWord(map, templeteName, savePath, fileName,
				"/templete");
		return fileName;
	}
	
	
	
	
	
